mapper.select:
SELECT 

FROM t_mesan_info a

WHERE a.id = #{id}

SELECT 

FROM t_mesan_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
		'%'||#{name}||'%'
		'%'+#{name}+'%'
		concat('%',#{name},'%')


		and a.mesan_code=#{mesanCode}


		AND t.status =#{approvalStatus} 


		and a.mesan_type=#{mesanType}

	${sqlMap.dataScopeFileForOffice}



		ORDER BY ${page.orderBy}


		ORDER BY a.is_viewtop desc, a.update_date DESC

SELECT 

FROM t_mesan_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
		'%'||#{name}||'%'
		'%'+#{name}+'%'
		concat('%',#{name},'%')

	and a.create_by = #{currentUser.id}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_mesan_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
update t_mesan_info
set is_viewtop = #{isViewTop},
update_date = #{updateDate}
WHERE id = #{id}

UPDATE t_mesan_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT t.id, t.`name`, t.mesan_type AS "mesanType", t.mesan_code as mesanCode, t.evaluate_score AS evaluateScore, tmd.name as mesanTypeName, count(tm.id) AS count, count(tr.id) AS downLoadCount, tur.browseTime AS browseTime, tur.browseCount FROM t_mesan_info t LEFT JOIN t_mesan_evaluate tm ON t.id = tm.mesan_id LEFT JOIN t_mesan_mark ta ON t.id = ta.mesan_id LEFT JOIN t_downlond_record tr ON t.id = tr.mseaninfo_id LEFT JOIN t_mesan_dir tmd on tmd.id = t.mesan_type LEFT JOIN ( SELECT tur.mesan_id, count(tur.id) as browseCount, sum( UNIX_TIMESTAMP( str_to_date(tur.end_time, '%Y-%m-%d %T') ) - UNIX_TIMESTAMP( str_to_date( tur.start_time, '%Y-%m-%d %T' ) ) ) AS browseTime FROM t_user_study_record_material tur GROUP BY tur.mesan_id ) tur ON t.id = tur.mesan_id
t.del_flag = #{DEL_FLAG_NORMAL}
AND t.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
group by t.id,t.name,t.mesan_type
ORDER BY ${page.orderBy}
ORDER BY t.update_date DESC
