mapper.select:
SELECT 

FROM t_downlond_record a

WHERE a.id = #{id}

SELECT 

FROM t_downlond_record a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{userId}


		AND a.mseaninfo_id = #{mseanInfoId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_downlond_record a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
