mapper.select:
SELECT 

FROM t_resource_info a

WHERE a.id = #{id}

SELECT 

FROM t_resource_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.resource_type = #{resourceType}


		AND a.resource_dir = #{resourceDir}


		AND a.is_submit = #{isSubmit}


		AND a.is_public = #{isPublic}


		AND a.resource_code = #{resourceCode}


		AND r.status = #{resourceStatus}

	${sqlMap.dataScopeFileForOffice}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

select a.id AS "id", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", u.name AS "updateBy.name", a.update_date AS "updateDate", a.remarks AS "remarks", a.del_flag AS "delFlag", a.name AS "name", a.resource_type AS "resourceType", a.resource_dir AS "resourceDir", d.name AS "resourceDirName", a.resource_url AS "resourceUrl", a.is_release AS "isRelease", a.is_createadmin as "isCreateAdmin", a.is_public AS "isPublic", a.resource_code as "resourceCode", r.status as "resourceStatus", a.resource_swf_url as "resourceSwfUrl", a.is_submit as "isSubmit" FROM t_resource_info a LEFT JOIN sys_user u on a.create_by = u.id LEFT JOIN t_resource_dir d on a.resource_dir = d.id LEFT JOIN t_approval_record r on a.id = r.resource_id and r.del_flag = 0 where a.del_flag = 0 and r.status =0 AND a.id NOT IN ( SELECT tr.id FROM t_distribu_temp t INNER JOIN t_resource_info tr ON t.ref_id = tr.id WHERE t.del_flag = 0 AND t.activity_dir_id = #{activityDirId} AND t.activity_id = #{activityId} )
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.resource_type = #{resourceType}
AND a.resource_dir = #{resourceDir}
AND a.is_submit = #{isSubmit}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_resource_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.resource_type = #{resourceType}


		AND a.resource_dir = #{resourceDir}


		AND a.is_submit = #{isSubmit}

	and a.create_by = #{currentUser.id}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_resource_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_resource_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE t_resource_info SET 
	is_public = #{isPublic}
WHERE id = #{id}

mapper.select:
SELECT 

FROM t_resource_info a


	LEFT JOIN sys_user_office uo ON uo.office_id = u.office_id AND uo.user_id =#{loginUserId}


	a.del_flag = #{DEL_FLAG_NORMAL} and r.status ='0'

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.resource_type = #{resourceType}


		AND a.is_public = #{isPublic}


		AND a.resource_dir = #{resourceDir}


		AND a.is_submit = #{isSubmit}


		AND(uo.user_id = #{loginUserId} OR a.create_by = #{loginUserId})




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
